import jsonData from "../data/data.json";

function indexData() {
  //Step 1: Query to Summary Mapping
  var keyword = [];
  var keywordSummary = [];
  //iterate through Query list
  for (var i = 0; i < jsonData.queries.length; i++) {
    var bookIndex = "";
    keyword = jsonData.queries[i].split(" ");
    // iterate through Book Summary List
    for (var j = 0; j < jsonData.summaries.length; j++) {
      var keywordCounter = 0;
      // For each keyword in query check if it is present in the current summary
      for (var k = 0; k < keyword.length; k++) {
        if (jsonData.summaries[j].summary.toLowerCase().includes(keyword[k])) {
          keywordCounter++;
        }
      }
      if (keywordCounter === keyword.length) {
        if (bookIndex === "") {
          bookIndex = j;
        } else {
          bookIndex = bookIndex + "," + j;
        }
      }
    }
    keywordSummary[i] = bookIndex;
  }
  // console.log("I am in Index Data:-" + keywordSummary);
  return keywordSummary;
}

export default indexData;
