import jsonData from "../data/data.json";

const search = (searhInput, keywordSummary = []) => {
  //Step 2: Search Utility
  var input = searhInput;
  var bookId = "";
  var noOfBooks = [];
  const maxSearchLimit = 10;
  var bookArray = [];
  var masterBookArray = [];
  var bookCounter = 0;
  for (
    var x = 0;
    x < jsonData.queries.length && bookCounter < maxSearchLimit;
    x++
  ) {
    if (jsonData.queries[x].includes(input)) {
      bookId = keywordSummary[x];
      if (bookId.length > 1) {
        noOfBooks = bookId.split(",");
      } else {
        noOfBooks.push(bookId);
      }

      while (bookCounter < maxSearchLimit && bookCounter < noOfBooks.length) {
        var bookTitle = jsonData.titles[noOfBooks[bookCounter]];
        var bookSummary = jsonData.summaries[noOfBooks[bookCounter]].summary;
        var bookAuthor = jsonData.authors[noOfBooks[bookCounter]].author;

        bookArray = [bookTitle, bookSummary, bookAuthor];

        masterBookArray.push(bookArray);
        bookCounter++;
      }
    }
  }
  return masterBookArray;
  //Return the above array from here
};

export default search;
