import React, { Component } from "react";
import "./Form.css";
import indexData from "../../Search/IndexData";
import search from "../../Search/Search";
import Book from "../Book/Book";

var querySummary = indexData();

class Form extends Component {
  // var result = [];
  // var bookResult = [];
  // var resultIndex;
  constructor(props) {
    super(props);
    // this.result = [];
    // this.resultIndex = 0;
    // this.bookResult = [];
    this.state = {
      searchInput: "",
      suggestions: [],
      result: [],
      resultIndex: 0,
      bookResult: [],
    };
  }

  changeHandler = (e) => {
    var searchInput = e.target.value;
    let suggestions = [];
    let result = [];
    if (searchInput.length > 0) {
      result = search(searchInput, querySummary);
      for (var i = 0; i < result.length; i++) {
        suggestions.push(result[i][0]);
      }
    }
    this.setState({
      searchInput: e.target.value,
      suggestions: suggestions,
    });
  };

  suggestionSelected(value, index) {
    this.setState(() => ({
      searchInput: value,
      suggestions: [],
    }));
    this.resultIndex = index;
  }

  renderSuggestions() {
    if (this.state.suggestions.length === 0) {
      return null;
    }
    return (
      <ul>
        {this.state.suggestions.map((item, index) => (
          <li key={index} onClick={() => this.suggestionSelected(item, index)}>
            {item}
          </li>
        ))}
      </ul>
    );
  }
  onSubmitHandler = (e) => {
    e.preventDefault();
    this.bookResult.push(this.result[this.resultIndex]);
  };

  render() {
    return (
      <form className="Form">
        <React.Fragment>
          <h1>Search Books</h1>
          <input
            data-testid="search-box"
            type="search"
            placeholder="Enter your search here"
            value={this.state.searchInput}
            onChange={(e) => this.changeHandler(e)}
          />
          <button
            data-testid="button"
            value="submit"
            onClick={(e) => this.onSubmitHandler(e)}
          >
            Submit
          </button>

          {this.renderSuggestions()}

          {this.bookResult.map((book, index) => (
            <div>
              <Book key={index} result={book} />
            </div>
          ))}
        </React.Fragment>
      </form>
    );
  }
}

export default Form;
