import React, { Component } from "react";
import "./Form.css";
import indexData from "../../Search/IndexData";
import search from "../../Search/Search";
import Book from "../Book/Book";

var keywordSummary = indexData();

class Form extends Component {
  constructor() {
    super(props);
    this.resultIndex;
    this.bookList = [];
    this.state = {
      searchInput: "",
      suggestions: [],
      result: [],
    };
  }

  changeHandler = (e) => {
    var searchInput = e.target.value;
    let suggestions = [];
    if (searchInput.includes(" ")) {
      result = search(this.state.searchInput, keywordSummary);
      for (var i = 0; i < result.length; i++) {
        suggestions.push(result[i][0]);
      }
    }
    this.setState({
      searchInput: e.target.value,
      suggestions: suggestions,
    });
  };

  suggestionSelected(value, index) {
    this.setState(() => ({
      searchInput: value,
      suggestions: [],
    }));
    resultIndex = index;
  }

  renderSuggestions() {
    if (this.state.suggestions.length === 0) {
      return null;
    }
    return (
      <ul>
        {this.state.suggestions.map((item, index) => (
          <li key={index} onClick={() => this.suggestionSelected(item, index)}>
            {item}
          </li>
        ))}
      </ul>
    );
  }
  onSubmitHandler = (e) => {
    e.preventDefault();
    this.setState({
      // result: result[resultIndex],
      result: result[resultIndex].push(),
      // concat(<Book result = { this.state.result } />),
      // result: result.concat(<Book result={this.state.result[resultIndex]} />),
      //   count: count++,
      // bookList: this.state.bookList.concat(<Book result={this.state.result} />),
    });
    // this.bookList.push(<Book result={this.state.result} />);
  };

  render() {
    return (
      <form className="Form">
        <React.Fragment>
          <h1>Search Books</h1>
          <input
            data-testid="search-box"
            type="search"
            placeholder="Enter your search here"
            value={this.state.searchInput}
            onChange={(e) => this.changeHandler(e)}
          />
          <button
            data-testid="button"
            value="submit"
            onClick={(e) => this.onSubmitHandler(e)}
          >
            Submit
          </button>
          {this.renderSuggestions()}
          {this.state.result && <Book result={this.state.result} />}
          {/* {this.state.result && result.concat(<Book result={this.state.result} />)} */}
          {/* {Array(count).fill(<Book result={this.state.result} />)} */}
          {/* {this.bookList.push(<Book result={this.state.result} />)} */}
          {/* {this.bookList.join(<Book result={this.state.result} />)} */}
          {/* {(e) => this.renderBooks(e, this)} */}
        </React.Fragment>
      </form>
    );
  }
}

export default Form;
