import React, { Component } from "react";
import "./Form.css";
import indexData from "../../Search/IndexData";
import search from "../../Search/Search";
import Book from "../Book/Book";

var querySummary = indexData();
// var result = [];
// var bookResult = [];
// var resultIndex;
class Form extends Component {
  constructor(props) {
    super(props);
    this.result = [];
    this.resultIndex = 0;
    this.bookResult = [];
    this.state = {
      searchInput: "",
      suggestions: [],
    };
  }

  changeHandler = (e) => {
    var searchInput = e.target.value;
    let suggestions = [];
    if (searchInput.length > 0) {
      this.result = search(this.state.searchInput, querySummary);
      for (var i = 0; i < this.result.length; i++) {
        suggestions.push(this.result[i][0]);
      }
    }
    this.setState({
      searchInput: e.target.value,
      suggestions: suggestions,
    });
  };

  suggestionSelected(value, index) {
    this.setState(() => ({
      searchInput: value,
      suggestions: [],
    }));
    this.resultIndex = index;
  }

  renderSuggestions() {
    if (this.state.suggestions.length === 0) {
      return null;
    }
    return (
      <ul>
        {this.state.suggestions.map((item, index) => (
          <li key={index} onClick={() => this.suggestionSelected(item, index)}>
            {item}
          </li>
        ))}
      </ul>
    );
  }
  onSubmitHandler = (e) => {
    e.preventDefault();
    bookResult.push(result[resultIndex]);
    this.setState({
      // result: result[resultIndex],
      // bookResult: this.state.bookResult.concat([
      //   result[resultIndex][0],
      //   result[resultIndex][1],
      //   result[resultIndex][2],
      // ]),
      // bookList: bookList.concat()
      // concat(<Book result = { this.state.result } />),
      // result: result.concat(<Book result={this.state.result[resultIndex]} />),
      //   count: count++,
      // bookList: this.state.bookList.concat(<Book result={this.state.result} />),
    });
  };

  render() {
    return (
      <form className="Form">
        <React.Fragment>
          <h1>Search Books</h1>
          <input
            data-testid="search-box"
            type="search"
            placeholder="Enter your search here"
            value={this.state.searchInput}
            onChange={(e) => this.changeHandler(e)}
          />
          <button
            data-testid="button"
            value="submit"
            onClick={(e) => this.onSubmitHandler(e)}
          >
            Submit
          </button>
          {this.renderSuggestions()}

          {/* {this.state.bookResult &&
            this.state.bookResult.map((item) => (
              <div>
                <Book result={item} />
              </div>
            ))} */}

          {bookResult.map((book, index) => (
            <div>
              <Book key={index} result={book} />
            </div>
          ))}

          {/* {this.state.bookResult &&
            <Book result={this.state.bookResult}  */}

          {/* <ul>
        {this.state.suggestions.map((item, index) => (
          <li key={index} onClick={() => this.suggestionSelected(item, index)}>
            {item}
          </li>
        ))}
      </ul> */}
        </React.Fragment>
      </form>
    );
  }
}

export default Form;
